import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Register from "./components/register.component";
import Login from "./components/login.component";
import Home from "./components/home.component";
import Logout from "./components/logout.component";
import Navbar from "./components/navbar.component";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
        username: "",
        loggedInStatus: false,
      };

    //const loggedInStatus = localStorage.getItem("loggedInStatus");
    this.state.loggedInStatus = localStorage.getItem("loggedInStatus");
  }

  onchange = () => {
    this.setState({ loggedInStatus: true });
    console.log('zalogowano');
    this.forceUpdate();
  };

  forceUpdate = () => {
    this.forceUpdate();
  };

  render() {
    return (
      <Router>
        <div className="container">
        <Navbar />
          <Switch>
            <Route path="/login" component={Login} loggedInStatus={this.state}/>
            <Route path="/register" component={Register} />
            <Route path="/logout" component={Logout} loggedInStatus={this.state} />
            <Route path="/" component={Home} />
          </Switch>
          <br />
        </div>
      </Router>
    );
  }
}
