import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Register from "./components/register.component";
import Login from "./components/login.component";
import Home from "./components/home.component";
import Logout from "./components/logout.component";
import Navbar from "./components/navbar.component";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.updateHandler = this.updateHandler.bind(this);

    this.state = {
      loggedInStatus: localStorage.getItem('loggedInStatus'),
      username: localStorage.getItem('username'),
    };


    this.updateHandler();
  }

  updateHandler = (status, username) => {
    console.log('update to: ' + status);
    this.setState({
      loggedInStatus: status,
      username: username
    })

    localStorage.setItem('loggedInStatus', status);
    localStorage.setItem('username', username);
  }

  componentDidMount() {
    this.setState({
      loggedInStatus: localStorage.getItem('loggedInStatus'),
      username: localStorage.getItem('username'),
    });
  }

  render() {
    return (
      <Router>
        <div className="container">
          <Navbar 
            loggedInStatus={this.state.loggedInStatus}
            updateHandler={this.updateHandler}
          />
          <Switch>
            <Route
              path="/login" 
              render={(props) => <Login updateHandler={this.updateHandler} {...props}/>}
            />
            <Route path="/register" component={Register} />
            <Route
              path="/logout"
              component={Logout}
            />
            <Route path="/"
              render={(props) => <Home username={this.state.username} loggedInStatus={this.state.loggedInStatus} {...props}/>}
            />
          </Switch>
          <br />
        </div>
      </Router>
    );
  }
}
