import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";

import "bootstrap/dist/css/bootstrap.min.css";

import Register from "./components/register.component";
import Login from "./components/login.component";
import Home from "./components/home.component";
import Logout from "./components/logout.component";
import Navbar from "./components/navbar.component";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
        username: "",
        loggedInStatus: false,
      };

    //const loggedInStatus = localStorage.getItem("loggedInStatus");
    this.state.loggedInStatus = localStorage.getItem("loggedInStatus");
  }

  loggedInStatus = () => {
    this.setState(loggedInStatus => ({ loggedInStatus: loggedInStatus }));
  };

  onLogin = () => {
    this.setState({ loggedInStatus: true });
  };

  onLogout = () => {
    this.setState({ loggedInStatus: false });
  };

  render() {
    return (
      <Router>
        <div className="container">
        <Navbar 
          loggedInStatus={this.state.loggedInStatus}
          onLogin={this.onLogin}
        />
          <Switch>
            <Route path="/login" component={Login} onLogout={this.onLogout}/>
            <Route path="/register" component={Register} />
            <Route path="/logout" component={Logout}  />
            <Route path="/" component={Home} />
          </Switch>
          <br />
        </div>
      </Router>
    );
  }
}
